package wifi.pojie;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;

import androidx.annotation.Nullable;

public class ErrorActivity extends Activity {
    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.error_activity);
        String error = getIntent().getStringExtra("error");
        TextView tvError = findViewById(R.id.tvError);
        tvError.setText(error != null ? error : "未知错误");
        Button btnExport = findViewById(R.id.btnExport);
        Button btnRestart = findViewById(R.id.btnRestart);
        btnExport.setOnClickListener(v -> {
            Intent shareIntent = new Intent(Intent.ACTION_SEND);
            shareIntent.setType("text/plain");
            shareIntent.putExtra(Intent.EXTRA_TEXT, tvError.getText().toString());
            startActivity(Intent.createChooser(shareIntent, "导出错误信息"));
        });
        btnRestart.setOnClickListener(v -> {
            Intent intent = new Intent(ErrorActivity.this, MainActivity.class);
            intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
            startActivity(intent);
            android.os.Process.killProcess(android.os.Process.myPid());
            System.exit(0);
        });
    }
}

package wifi.pojie;

import android.app.Activity;
import android.content.ClipData;
import android.content.ClipboardManager;
import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;

import androidx.annotation.Nullable;

public class ErrorActivity extends Activity {
    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.error_activity);
        String error = getIntent().getStringExtra("error");
        TextView tvError = findViewById(R.id.tvError);
        tvError.setText(error != null ? error : "未知错误");
        Button btnExport = findViewById(R.id.btnExport);
        Button btnRestart = findViewById(R.id.btnRestart);
        Button btnCopy = findViewById(R.id.btnCopy);
        btnExport.setOnClickListener(v -> {
            Intent shareIntent = new Intent(Intent.ACTION_SEND);
            shareIntent.setType("text/plain");
            shareIntent.putExtra(Intent.EXTRA_TEXT, tvError.getText().toString());
            startActivity(Intent.createChooser(shareIntent, "导出错误信息"));
        });
        btnRestart.setOnClickListener(v -> {
            Intent intent = new Intent(ErrorActivity.this, MainActivity.class);
            intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
            startActivity(intent);
            android.os.Process.killProcess(android.os.Process.myPid());
            System.exit(0);
        });
        btnCopy.setOnClickListener(v -> {
            ClipboardManager clipboard = (ClipboardManager) getSystemService(CLIPBOARD_SERVICE);
            ClipData clip = ClipData.newPlainText("错误信息", tvError.getText().toString());
            clipboard.setPrimaryClip(clip);
        });
    }
}

package wifi.pojie;

import android.app.Application;
import android.content.Intent;

public class WifiApplication extends Application {
    @Override
    public void onCreate() {
        super.onCreate();
        Thread.setDefaultUncaughtExceptionHandler((thread, throwable) -> {
            StringBuilder sb = new StringBuilder();
            sb.append("线程: ").append(thread.getName()).append("\n");
            sb.append("异常: ").append(throwable).append("\n\n");
            for (StackTraceElement element : throwable.getStackTrace()) {
                sb.append(element.toString()).append("\n");
            }
            Intent intent = new Intent(getApplicationContext(), ErrorActivity.class);
            intent.putExtra("error", sb.toString());
            intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
            startActivity(intent);
            android.os.Process.killProcess(android.os.Process.myPid());
            System.exit(10);
        });
    }
}

package wifi.pojie;

import android.app.Application;
import android.content.Intent;

public class WifiApplication extends Application {
    @Override
    public void onCreate() {
        super.onCreate();
        Thread.setDefaultUncaughtExceptionHandler((thread, throwable) -> {
            StringBuilder sb = new StringBuilder();
            sb.append("设备型号: ").append(android.os.Build.MODEL).append("\n");
            sb.append("系统版本: ").append(android.os.Build.VERSION.RELEASE).append("\n");
            sb.append("API等级: ").append(android.os.Build.VERSION.SDK_INT).append("\n");
            sb.append("处理器型号: ").append(android.os.Build.HARDWARE).append("\n");
            sb.append("系统架构: ").append(android.os.Build.SUPPORTED_ABIS != null ? android.os.Build.SUPPORTED_ABIS[0] : "未知").append("\n\n");
            sb.append("线程: ").append(thread.getName()).append("\n");
            sb.append("异常: ").append(throwable).append("\n\n");
            for (StackTraceElement element : throwable.getStackTrace()) {
                sb.append(element.toString()).append("\n");
            }
            Intent intent = new Intent(getApplicationContext(), ErrorActivity.class);
            intent.putExtra("error", sb.toString());
            intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
            startActivity(intent);
            android.os.Process.killProcess(android.os.Process.myPid());
            System.exit(10);
        });
    }
}
